import Utils from 'phase/utils';
import UserStatus from 'phase/userstatus';

class User {
    private id: number;
    private name: string;
    private formatName: string;
    private systemName: string;
    private avatar: string;
    private status: UserStatus;

    constructor(id: number, name: string, systemName: string, avatar: string, clientSystemName: string, status: UserStatus) {
        this.id = id;
        this.name = name;
        this.systemName = systemName;
        this.avatar = avatar[0] === "/" ? "https://t.dark-gaming.com:3001"+avatar : avatar;
        this.status = status;

        // Users from other systems will have a suffix to show for it
        if (systemName !== clientSystemName) {
            this.formatName = `${Utils.htmlspecialchars(name)}@<i>${systemName}</i>`;
        } else {
            this.formatName = Utils.htmlspecialchars(name)
        }
    }

    /**
     * Gets this users Id
     */
    public getId(): number {
        return this.id;
    }

    /**
     * Gets the username for this user
     */
    public getName(): string {
        return this.name;
    }

    /**
     * Gets the username combined with an appropriate system name for this user
     */
    public getFormatName(): string {
        return this.formatName;
    }

    /**
     * Gets this users avatar URL
     */
    public getAvatar(): string {
        return this.avatar;
    }

    /**
     * Gets the activity status of this user
     */
    public getStatus(): UserStatus {
        return this.status;
    }

    public setStatus(status: UserStatus): void {
        this.status = status;
    }

    public setAvatar(avatar: string): void {
        this.avatar = avatar;
    }
}

export default User;