import Glpyh from "phasecore/glyph";

class Glphys {
    public static getGlyphImageNameFromID(id) {
        // Fallback
        if (typeof Glpyh.ids[id] === "undefined") {
            return "no index found";
        }

        if (id >= 0) {
            return encodeURIComponent(Glpyh.ids[id].replace(/ /g, "_"));
        } else {
            // Return at least something...
            return "bad id";
        }
    }
}

export default Glphys;
