import Item from "phasecore/item";

class Items {
    public static getItemNameFromID(id) {
        if (id > 0) {
            return Item.ids[id];
        } else if (id < 0) {
            return Item.negativeIds[id];
        } else {
            // Return at least something...
            return "Beenade";
        }
    }

    public static getItemImageNameFromID(id) {
        // Fallback
        if (typeof Item.ids[id] === "undefined" && typeof Item.negativeIds[id] === "undefined") {
            return "Beenade";
        }

        if (id > 0) {
            return encodeURIComponent(Item.ids[id].replace(/ /g, "_"));
        } else if (id < 0) {
            return encodeURIComponent(Item.negativeIds[id].replace(/ /g, "_"));
        } else {
            // Return at least something...
            return "Beenade";
        }
    }
}

export default Items;
