interface Config {
    // Whether or not to combine messages from the same user, posted consecutively in a short time frame
    combineMessages: boolean;

    // Whether or not to shrink join/leave messages
    shrinkJoinLeaveMessages: boolean;

    // Whether or not to not display (and not request) join/leave messages
    useAndDisplayJoinLeaveMessages: boolean;

    // Whether live typing should go with complete chat, causing it to go up as new chat messages are received
    mergeLiveTypingWithCompleteChatArea: boolean;
}

export default Config;
