class DiscussionSection {
    public static main = "#content-discussions";
    public static selected = ".selected";
    public static list = "#discussion-list";
    public static discussion = ".discussion";
    public static unread = ".unread";
    public static unreadInprogress = ".unread-inprogress";
    public static preview = ".discussion-preview";
    public static addDiscussion = "#add-discussion";
    public static identifier = function(discussionId: string): string {
        return `#disc${discussionId}`;
    };
}

export default DiscussionSection;
