import $ from "jquery";
import Client from "phaseweb/client";
import Events from "phaseweb/pagemodel/avatarupload/events";
import template from "phaseweb/pagemodel/avatarupload/template";

class AvatarUpload {
    private _client: Client;
    private _events: Events;

    constructor(client: Client) {
        this._client = client;
        $("body").prepend(template);
    }

    public registerEvents(): void {
        this._events = new Events(this._client);
    }
}

export default AvatarUpload;
