import ElementIdentifiers from "phaseweb/elementidentifiers";

function AddDiscussionUser() {
    return `<div id="${ElementIdentifiers.PeopleSection.addDiscussionUserForm.substr(1)}">
                <div id="${ElementIdentifiers.PeopleSection.addDiscussionUserEntry.substr(1)}">
                    <div class="people_user_avatar">
                        <img src="https://dark-gaming.com/img/default.png">
                    </div>
                    <input id="${ElementIdentifiers.PeopleSection.addDiscussionUserInput.substr(1)}" placeholder="Enter a username..." type="text">
                </div>
                <div id="${ElementIdentifiers.PeopleSection.addDiscussionUserSearchResults.substr(1)}"></div>
            </div>`;
}

export default AddDiscussionUser;
