import User from 'phase/user';
import UserStatus from 'phase/userstatus';

function PeopleEntry(user: User): string {
    let iconClass = user.getStatus() === UserStatus.Offline ? ' user-status-offline' : '';
    let title = user.getStatus() === UserStatus.Offline ? 'Offline' : 'Online';
    return `
    <li>
        <div class="people_user_avatar">
            <img class="avatar_${user.getId()}" src="${user.getAvatar()}"/>
        </div>
        <div class="people_user_right">
            <span class="people-username">${user.getFormatName()}</span>
        </div>
        <i class="fa fa-circle${iconClass}" data-toggle="tooltip" data-placement="left" title="${title}"></i>
    </li>`
}

export default PeopleEntry;