import SafeString from "phaseclient/safestring";
import ElementIdentifiers from "phaseweb/elementidentifiers";

function SearchUserResult(clientSystemName: string, username: string, systemName: string, avatar: string) {
    const safeUsername = new SafeString(username).getContentWithHtmlEscaped();
    const safeSystemName = new SafeString(systemName).getContentWithHtmlEscaped();
    const safeClientSystemName = new SafeString(clientSystemName).getContentWithHtmlEscaped();
    if (avatar.indexOf("/") === 0) {
        avatar = `https://t.dark-gaming.com:3001${avatar}`;
    }

    return `<div class="adduser-search-result">
                <div class="people_user_avatar">
                    <img src="${avatar}">
                </div>
                <div class="adduser-search-result-name">
                    ${safeUsername}${safeClientSystemName !== safeSystemName ? `@${safeSystemName}` : ``}
                </div>
            </div>`;
}

export default SearchUserResult;
