import Message from "phasecore/message";
import VWatcher from "vwatcher/racial";

export default class ProfanityWatcher extends VWatcher {
    public Matchers : RegExp[];

    constructor() {
        super();
        this.Matchers = [
            /f+a+g+o+t+/g,
            /s+l+u+t/g,
        ]
    }

    public IsViolating(msg: Message) : boolean {
        return this.Matchers.some(r => r.test(msg.content.asIs()) || r.test(msg.username.asIs()) || r.test(msg.guestName.asIs()));
    }
}