interface NetworkOptions {
    port?: number,
    hostname?: string,
    path?: string,
    secure?: boolean,
    autoReconnect?: boolean,
    autoProcessSubscriptions: boolean,
    connectTimeout: number,
    ackTimeout: number,
    timestampRequests: boolean,
    timestampParam: 't',
    authEngine: null,
    authTokenName: 'socketCluster.authToken',
    binaryType: 'arraybuffer',
    multiplex: true,
    cloneData: false
}